import React from 'react';
import { View, Text, StyleSheet, Image, ScrollView } from 'react-native';
import { COLORS } from '../../App';

const Schedule = ({ tasks, category }) => {
  // Filter tasks by selected category
  const filteredTasks = tasks.filter(task => task.category === category);

  // Group tasks by hour for timeline view
  const groupedByHour = filteredTasks.reduce((acc, task) => {
    const hour = new Date(task.startTime).getHours();
    if (!acc[hour]) acc[hour] = [];
    acc[hour].push(task);
    return acc;
  }, {});

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Your {category.charAt(0).toUpperCase() + category.slice(1)} Schedule</Text>

      <ScrollView style={styles.timeline}>
        {Object.keys(groupedByHour).sort().map(hour => (
          <View key={hour} style={styles.timeBlock}>
            <Text style={styles.timeLabel}>{hour}:00</Text>
            <View style={styles.taskContainer}>
              {groupedByHour[hour].map((task, index) => (
                <View key={index} style={styles.task}>
                  <Text style={styles.taskTitle}>{task.title}</Text>
                  <Text style={styles.taskDuration}>{task.duration} min</Text>
                  {task.image && (
                    <Image 
                      source={{ uri: task.image }} 
                      style={styles.taskImage} 
                    />
                  )}
                  {task.productivityLevel && (
                    <View style={[styles.productivityIndicator, 
                      { backgroundColor: getProductivityColor(task.productivityLevel) }]}>
                      <Text style={styles.productivityText}>
                        {task.productivityLevel}
                      </Text>
                    </View>
                  )}
                </View>
              ))}
            </View>
          </View>
        ))}

        {/* Empty state */}
        {filteredTasks.length === 0 && (
          <Text style={styles.emptyState}>
            No tasks scheduled for {category} category. Add some tasks to get personalized suggestions!
          </Text>
        )}
      </ScrollView>
    </View>
  );
};

// Helper function to get color based on productivity level
const getProductivityColor = (level) => {
  switch (level) {
    case 'high': return COLORS.success;
    case 'medium': return COLORS.warning;
    case 'low': return COLORS.danger;
    default: return COLORS.secondaryDark;
  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginBottom: 20,
  },
  title: {
    fontSize: 22,
    fontWeight: 'bold',
    marginBottom: 15,
    color: COLORS.black,
  },
  timeline: {
    flex: 1,
  },
  timeBlock: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  timeLabel: {
    width: 50,
    fontSize: 16,
    color: COLORS.secondaryDark,
    paddingTop: 5,
  },
  taskContainer: {
    flex: 1,
  },
  task: {
    backgroundColor: COLORS.cardBackground,
    borderRadius: 8,
    padding: 12,
    marginBottom: 8,
    shadowColor: COLORS.black,
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.2,
    shadowRadius: 1,
    elevation: 2,
    borderLeftWidth: 4,
    borderLeftColor: COLORS.primary,
  },
  taskTitle: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 5,
    color: COLORS.black,
  },
  taskDuration: {
    fontSize: 14,
    color: COLORS.secondaryDark,
    marginBottom: 5,
  },
  taskImage: {
    width: '100%',
    height: 120,
    borderRadius: 6,
    marginVertical: 5,
  },
  productivityIndicator: {
    alignSelf: 'flex-start',
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 12,
    marginTop: 5,
  },
  productivityText: {
    color: COLORS.white,
    fontSize: 12,
    fontWeight: 'bold',
  },
  emptyState: {
    fontSize: 16,
    color: COLORS.secondaryDark,
    fontStyle: 'italic',
    textAlign: 'center',
    marginTop: 40,
    backgroundColor: COLORS.secondaryLight,
    padding: 20,
    borderRadius: 8,
  },
});

export default Schedule;
