import React from 'react';
import { View, Text, StyleSheet, ScrollView, Platform } from 'react-native';
import COLORS from '../../colors';
import '../../../src/styles/components/DailySchedule.css';

const DailySchedule = ({ tasks = [] }) => {
  // Constants for timeline calculations
  const TOTAL_MINUTES_IN_DAY = 24 * 60; // 24 hours = 1440 minutes
  const TIMELINE_WIDTH = 1440; // 1 minute = 1 pixel for simplicity

  /**
   * Converts a time string (e.g., "12:00 AM") to minutes since midnight
   * @param {string} timeString - Time in format "HH:MM AM/PM"
   * @return {number} Minutes since midnight
   */
  const convertTimeToMinutes = (timeString) => {
    if (!timeString) return 0;

    try {
      // Parse the time string
      const parts = timeString.split(' ');
      if (parts.length !== 2) return 0;

      const [time, period] = parts;
      const timeParts = time.split(':');
      if (timeParts.length !== 2) return 0;

      let hours = parseInt(timeParts[0], 10);
      let minutes = parseInt(timeParts[1], 10);

      if (isNaN(hours) || isNaN(minutes)) return 0;

      // Convert to 24-hour format
      if (period === 'PM' && hours < 12) {
        hours += 12;
      } else if (period === 'AM' && hours === 12) {
        hours = 0;
      }

      // Calculate total minutes since midnight
      return hours * 60 + minutes;
    } catch (error) {
      console.error("Error parsing time:", error);
      return 0;
    }
  };

  /**
   * Converts minutes since midnight to a percentage position on the timeline
   * @param {number} minutes - Minutes since midnight
   * @return {number} Percentage position (0-100)
   */
  const getPositionPercentage = (minutes) => {
    return (minutes / TOTAL_MINUTES_IN_DAY) * 100;
  };

  /**
   * Converts minutes to a width percentage on the timeline
   * @param {number} durationMinutes - Duration in minutes
   * @return {number} Width percentage (0-100)
   */
  const getDurationWidthPercentage = (durationMinutes) => {
    return (durationMinutes / TOTAL_MINUTES_IN_DAY) * 100;
  };

  /**
   * Formats time for display (e.g., 9:30 AM)
   * @param {number} minutes - Minutes since midnight
   * @return {string} Formatted time string
   */
  const formatTime = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;

    // Convert to 12-hour format
    const period = hours >= 12 ? 'PM' : 'AM';
    const displayHours = hours % 12 === 0 ? 12 : hours % 12;

    return `${displayHours}:${mins.toString().padStart(2, '0')} ${period}`;
  };

  /**
   * Generates hour markers for the timeline
   * @return {Array} JSX elements for hour markers
   */
  const renderHourMarkers = () => {
    const markers = [];
    for (let i = 0; i <= 24; i++) {
      const position = (i / 24) * 100;
      markers.push(
        <div 
          key={i} 
          className="hour-marker"
          style={{ position: 'absolute', left: `${position}%` }}
        >
          {i % 3 === 0 && (
            <span className="hour-label">
              {i === 0 ? '12 AM' : i === 12 ? '12 PM' : i > 12 ? `${i-12} PM` : `${i} AM`}
            </span>
          )}
        </div>
      );
    }
    return markers;
  };

  // Sort tasks by start time
  const sortedTasks = [...tasks].sort((a, b) => {
    return convertTimeToMinutes(a.startTime) - convertTimeToMinutes(b.startTime);
  });

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Daily Schedule</Text>

      {/* Web-specific timeline view */}
      {Platform.OS === 'web' && (
        <div className="daily-schedule-container">
          <div className="timeline-container">
            <div className="hour-markers">
              {renderHourMarkers()}
            </div>

            <div className="timeline">
              {sortedTasks.map((task, index) => {
                if (!task || !task.startTime || !task.duration) {
                  return null; // Skip invalid tasks
                }

                try {
                  // Calculate position and width based on start time and duration
                  const startMinutes = convertTimeToMinutes(task.startTime);
                  const positionPercentage = getPositionPercentage(startMinutes);
                  const widthPercentage = getDurationWidthPercentage(parseInt(task.duration, 10) || 30);

                  // Ensure minimum width for very short tasks
                  const actualWidth = Math.max(widthPercentage, 2);

                  return (
                    <div
                      key={index}
                      className="task-block"
                      style={{
                        left: `${positionPercentage}%`,
                        width: `${actualWidth}%`, // Use actualWidth instead of widthPercentage
                        backgroundColor: task.color || COLORS.primary,
                      }}
                    >
                      <div className="task-title">{task.name || 'Untitled Task'}</div>
                      <div className="task-duration">{task.duration || 0} min</div>
                      <div className="time-label">{task.startTime}</div>
                    </div>
                  );
                } catch (error) {
                  console.error("Error rendering task:", error, task);
                  return null;
                }
              })}
            </div>
          </div>
        </div>
      )}

      {/* Mobile-specific timeline view (simplified) */}
      {Platform.OS !== 'web' && (
        <ScrollView horizontal={true} showsHorizontalScrollIndicator={false} style={styles.mobileTimeline}>
          <View style={styles.timelineContent}>
            {/* Hour markers for mobile */}
            <View style={styles.hourMarkersContainer}>
              {Array.from({ length: 25 }).map((_, i) => (
                <View key={i} style={styles.hourMarker}>
                  {i % 3 === 0 && (
                    <Text style={styles.hourLabel}>
                      {i === 0 ? '12 AM' : i === 12 ? '12 PM' : i > 12 ? `${i-12} PM` : `${i} AM`}
                    </Text>
                  )}
                </View>
              ))}
            </View>

            {/* Task blocks for mobile */}
            <View style={styles.taskBlocksContainer}>
              {sortedTasks.map((task, index) => {
                if (!task || !task.startTime || !task.duration) {
                  return null; // Skip invalid tasks
                }

                try {
                  const startMinutes = convertTimeToMinutes(task.startTime);
                  const leftPosition = (startMinutes / TOTAL_MINUTES_IN_DAY) * TIMELINE_WIDTH;
                  const duration = parseInt(task.duration, 10) || 30;
                  const width = (duration / TOTAL_MINUTES_IN_DAY) * TIMELINE_WIDTH;

                  return (
                    <View
                      key={index}
                      style={[
                        styles.taskBlock,
                        {
                          left: leftPosition,
                          width: Math.max(width, 60), // Minimum width for visibility
                          backgroundColor: task.color || COLORS.primary
                        }
                      ]}
                    >
                      <Text style={styles.taskTitle} numberOfLines={1}>{task.name || 'Untitled Task'}</Text>
                      <Text style={styles.taskDuration}>{task.duration || 0} min</Text>
                      <Text style={styles.timeLabel}>{task.startTime}</Text>
                    </View>
                  );
                } catch (error) {
                  console.error("Error rendering mobile task:", error, task);
                  return null;
                }
              })}
            </View>
          </View>
        </ScrollView>
      )}

      {tasks.length === 0 && (
        <View style={styles.emptyState}>
          <Text style={styles.emptyStateText}>
            No tasks scheduled for today. Add tasks to see your daily schedule.
          </Text>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: COLORS.cardBackground,
    borderRadius: 12,
    padding: 16,
    marginVertical: 10,
    shadowColor: COLORS.black,
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
    borderLeftWidth: 4,
    borderLeftColor: COLORS.primary,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 15,
    color: COLORS.black,
  },
  emptyState: {
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: COLORS.secondaryLight,
    borderRadius: 8,
  },
  emptyStateText: {
    color: COLORS.secondaryDark,
    textAlign: 'center',
    fontStyle: 'italic',
  },
  // Mobile-specific styles
  mobileTimeline: {
    height: 100,
    marginVertical: 10,
  },
  timelineContent: {
    width: TIMELINE_WIDTH,
    height: 80,
    position: 'relative',
  },
  hourMarkersContainer: {
    flexDirection: 'row',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: 20,
  },
  hourMarker: {
    width: TIMELINE_WIDTH / 24,
    height: 10,
    borderRightWidth: 1,
    borderRightColor: COLORS.secondary,
    position: 'relative',
  },
  hourLabel: {
    position: 'absolute',
    top: -15,
    left: 0,
    fontSize: 10,
    color: COLORS.secondaryDark,
  },
  taskBlocksContainer: {
    position: 'absolute',
    top: 25,
    left: 0,
    right: 0,
    height: 60,
  },
  taskBlock: {
    position: 'absolute',
    height: 50,
    borderRadius: 4,
    padding: 5,
    justifyContent: 'center',
  },
  taskTitle: {
    color: COLORS.white,
    fontWeight: 'bold',
    fontSize: 12,
  },
  taskDuration: {
    color: COLORS.white,
    fontSize: 10,
    opacity: 0.9,
  },
  timeLabel: {
    color: COLORS.white,
    fontSize: 9,
    opacity: 0.9,
  },
});

export default DailySchedule;
