import React, { useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  SafeAreaView, 
  StatusBar,
  ScrollView
} from 'react-native';

// Import components one by one
import CategorySelector from './src/components/CategorySelector';
import Schedule from './src/components/Schedule';
import TaskInput from './src/components/TaskInput';
import SuggestionPanel from './src/components/SuggestionPanel';

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [category, setCategory] = useState('work'); // Default category

  const addTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar barStyle="dark-content" />
      <ScrollView 
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
      >
        <Text style={styles.appTitle}>Daily Schedule Optimizer</Text>

        <CategorySelector 
          selectedCategory={category} 
          onSelectCategory={setCategory} 
        />

        <Schedule 
          tasks={tasks} 
          category={category}
        />

        <TaskInput 
          onAddTask={addTask} 
          category={category}
        />

        <SuggestionPanel 
          tasks={tasks} 
          category={category}
        />
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    padding: 16,
    paddingTop: 40, // Add padding for status bar
  },
  appTitle: {
    fontSize: 26,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 20,
    textAlign: 'center',
  },
});
