import React from 'react';
import { View, Text, StyleSheet, ScrollView, Platform } from 'react-native';
import COLORS from '../../colors';
import '../../../src/styles/components/DailySchedule.css';

// Constants for timeline calculations - defined outside component for StyleSheet access
const TOTAL_MINUTES_IN_DAY = 24 * 60; // 24 hours = 1440 minutes
const MINUTES_PER_PIXEL = 2; // 1 minute = 2 pixels height (as requested)
const DEFAULT_MIN_TIMELINE_HEIGHT = 400; // Minimum height when tasks exist

const DailySchedule = ({ tasks = [] }) => {

  /**
   * Converts a time string (e.g., "12:00 AM") to minutes since midnight
   * @param {string} timeString - Time in format "HH:MM AM/PM"
   * @return {number} Minutes since midnight
   */
  const convertTimeToMinutes = (timeString) => {
    if (!timeString) return 0;

    try {
      // Parse the time string
      const parts = timeString.split(' ');
      if (parts.length !== 2) return 0;

      const [time, period] = parts;
      const timeParts = time.split(':');
      if (timeParts.length !== 2) return 0;

      let hours = parseInt(timeParts[0], 10);
      let minutes = parseInt(timeParts[1], 10);

      if (isNaN(hours) || isNaN(minutes)) return 0;

      // Convert to 24-hour format
      if (period === 'PM' && hours < 12) {
        hours += 12;
      } else if (period === 'AM' && hours === 12) {
        hours = 0;
      }

      // Calculate total minutes since midnight
      return hours * 60 + minutes;
    } catch (error) {
      console.error("Error parsing time:", error);
      return 0;
    }
  };

  /**
   * Converts minutes since midnight to a vertical position in pixels
   * @param {number} minutes - Minutes since midnight
   * @return {number} Vertical position in pixels
   */
  const getVerticalPosition = (minutes) => {
    if (!minutes || isNaN(minutes)) return 0;
    // Convert minutes to pixels (1 minute = 1 pixel by default)
    const position = minutes / MINUTES_PER_PIXEL;
    return Math.min(Math.max(position, 0), TIMELINE_HEIGHT); // Clamp between 0 and max height
  };

  /**
   * Converts duration in minutes to height in pixels
   * @param {number} durationMinutes - Duration in minutes
   * @return {number} Height in pixels
   */
  const getDurationHeight = (durationMinutes) => {
    if (!durationMinutes || isNaN(durationMinutes)) return 30; // Default minimum height
    // Convert duration to height (1 minute = 1 pixel by default)
    const height = durationMinutes / MINUTES_PER_PIXEL;
    // Ensure height is reasonable (minimum 30px for visibility)
    return Math.max(height, 30);
  };

  /**
   * Formats time for display (e.g., 9:30 AM)
   * @param {number} minutes - Minutes since midnight
   * @return {string} Formatted time string
   */
  const formatTime = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;

    // Convert to 12-hour format
    const period = hours >= 12 ? 'PM' : 'AM';
    const displayHours = hours % 12 === 0 ? 12 : hours % 12;

    return `${displayHours}:${mins.toString().padStart(2, '0')} ${period}`;
  };

  /**
   * Generates hour markers for the vertical timeline
   * @return {Array} JSX elements for hour markers
   */
  const renderHourMarkers = () => {
    try {
      const markers = [];
      const hourGridLines = [];

      // Create markers for each hour (0-24)
      for (let i = 0; i <= 24; i++) {
        const minutes = i * 60; // Convert hour to minutes
        const position = getVerticalPosition(minutes);

        // Create hour marker with label
        markers.push(
          <div 
            key={i} 
            className="hour-marker"
            style={{ top: `${position}px` }}
          >
            <span className="hour-label">
              {i === 0 ? '12 AM' : i === 12 ? '12 PM' : i > 12 ? `${i-12} PM` : `${i} AM`}
            </span>
          </div>
        );

        // Create grid line for this hour
        hourGridLines.push(
          <div
            key={`grid-${i}`}
            className="hour-grid-line"
            style={{ top: `${position}px` }}
          />
        );
      }

      return { markers, hourGridLines };
    } catch (error) {
      console.error("Error rendering hour markers:", error);
      return { markers: [], hourGridLines: [] };
    }
  };

  // Sort tasks by start time with error handling
  const sortedTasks = React.useMemo(() => {
    try {
      return [...tasks].filter(task => task && task.startTime).sort((a, b) => {
        return convertTimeToMinutes(a.startTime) - convertTimeToMinutes(b.startTime);
      });
    } catch (error) {
      console.error("Error sorting tasks:", error);
      return [];
    }
  }, [tasks]);

  // Get the hour markers and grid lines
  const { markers, hourGridLines } = renderHourMarkers();

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Daily Schedule</Text>

      {/* Vertical Timeline View */}
      {Platform.OS === 'web' && (
        <div className="daily-schedule-container">
          <div className="timeline-container">
            {/* Hour markers column */}
            <div className="hour-markers">
              {markers}
            </div>

            {/* Timeline with tasks */}
            <div className="timeline">
              {/* Hour grid lines */}
              {hourGridLines}

              {/* Task blocks */}
              {sortedTasks.length > 0 ? (
                sortedTasks.map((task, index) => {
                  if (!task || !task.startTime || !task.duration) {
                    return null; // Skip invalid tasks
                  }

                  try {
                    // Calculate position and height based on start time and duration
                    const startMinutes = convertTimeToMinutes(task.startTime);
                    const topPosition = getVerticalPosition(startMinutes);
                    const height = getDurationHeight(parseInt(task.duration, 10) || 30);

                    return (
                      <div
                        key={index}
                        className="task-block"
                        style={{
                          top: `${topPosition}px`,
                          height: `${height}px`,
                          backgroundColor: task.color || COLORS.primary,
                        }}
                      >
                        <div className="task-title">{task.name || 'Untitled Task'}</div>
                        <div className="task-details">
                          {task.startTime} • {task.duration || 0} min
                        </div>
                      </div>
                    );
                  } catch (error) {
                    console.error("Error rendering task:", error, task);
                    return null;
                  }
                })
              ) : (
                <div className="empty-timeline">
                  No tasks scheduled for today. Add tasks to see your daily schedule.
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Mobile Timeline View */}
      {Platform.OS !== 'web' && (
        <ScrollView style={styles.mobileTimeline}>
          <View style={[styles.timelineContent, { height: TIMELINE_HEIGHT }]}>
            {/* Hour markers for mobile */}
            <View style={styles.hourMarkersContainer}>
              {Array.from({ length: 25 }).map((_, i) => {
                const minutes = i * 60;
                const position = minutes / MINUTES_PER_PIXEL;

                return (
                  <View 
                    key={i} 
                    style={[
                      styles.hourMarker,
                      { top: position }
                    ]}
                  >
                    <Text style={styles.hourLabel}>
                      {i === 0 ? '12 AM' : i === 12 ? '12 PM' : i > 12 ? `${i-12} PM` : `${i} AM`}
                    </Text>
                    <View style={styles.hourLine} />
                  </View>
                );
              })}
            </View>

            {/* Task blocks for mobile */}
            <View style={styles.taskBlocksContainer}>
              {sortedTasks.length > 0 ? (
                sortedTasks.map((task, index) => {
                  if (!task || !task.startTime || !task.duration) {
                    return null; // Skip invalid tasks
                  }

                  try {
                    const startMinutes = convertTimeToMinutes(task.startTime);
                    const topPosition = startMinutes / MINUTES_PER_PIXEL;
                    const duration = parseInt(task.duration, 10) || 30;
                    const height = Math.max(duration / MINUTES_PER_PIXEL, 30); // Minimum height 30px

                    return (
                      <View
                        key={index}
                        style={[
                          styles.taskBlock,
                          {
                            position: 'absolute',
                            top: topPosition,
                            height: height,
                            left: 0,
                            right: 0,
                            backgroundColor: task.color || COLORS.primary
                          }
                        ]}
                      >
                        <Text style={styles.taskTitle} numberOfLines={1}>
                          {task.name || 'Untitled Task'}
                        </Text>
                        <Text style={styles.taskDuration}>
                          {task.startTime} • {task.duration || 0} min
                        </Text>
                      </View>
                    );
                  } catch (error) {
                    console.error("Error rendering mobile task:", error, task);
                    return null;
                  }
                })
              ) : (
                <View style={styles.emptyState}>
                  <Text style={styles.emptyStateText}>
                    No tasks scheduled for today. Add tasks to see your daily schedule.
                  </Text>
                </View>
              )}
            </View>
          </View>
        </ScrollView>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: COLORS.cardBackground,
    borderRadius: 12,
    padding: 16,
    marginVertical: 10,
    shadowColor: COLORS.black,
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
    borderLeftWidth: 4,
    borderLeftColor: COLORS.primary,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 15,
    color: COLORS.black,
  },
  emptyState: {
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: COLORS.secondaryLight,
    borderRadius: 8,
    marginTop: 20,
  },
  emptyStateText: {
    color: COLORS.secondaryDark,
    textAlign: 'center',
    fontStyle: 'italic',
  },

  // Mobile-specific styles for vertical timeline
  mobileTimeline: {
    height: 500, // Fixed height with scroll
    marginVertical: 10,
  },
  timelineContent: {
    position: 'relative',
    flexDirection: 'row',
  },
  hourMarkersContainer: {
    width: 60,
    position: 'absolute',
    top: 0,
    left: 0,
    bottom: 0,
    borderRightWidth: 1,
    borderRightColor: COLORS.secondary,
  },
  hourMarker: {
    position: 'absolute',
    left: 0,
    width: 60,
    alignItems: 'flex-start',
  },
  hourLabel: {
    fontSize: 10,
    color: COLORS.secondaryDark,
    marginLeft: 5,
  },
  hourLine: {
    position: 'absolute',
    left: 60,
    right: -500, // Extend across timeline
    height: 1,
    backgroundColor: 'rgba(0,0,0,0.05)',
  },
  taskBlocksContainer: {
    position: 'absolute',
    top: 0,
    left: 70, // Offset from hour markers
    right: 10,
    bottom: 0,
  },
  taskBlock: {
    borderRadius: 4,
    padding: 8,
    justifyContent: 'center',
  },
  taskTitle: {
    color: COLORS.white,
    fontWeight: 'bold',
    fontSize: 12,
  },
  taskDuration: {
    color: COLORS.white,
    fontSize: 10,
    opacity: 0.9,
    marginTop: 4,
  },
});

export default DailySchedule;
