import React, { useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  SafeAreaView, 
  StatusBar,
  ScrollView
} from 'react-native';

// Import components one by one
import CategorySelector from './src/components/CategorySelector';
import Schedule from './src/components/Schedule';
import TaskInput from './src/components/TaskInput';
import SuggestionPanel from './src/components/SuggestionPanel';
import MoodTracker from './src/components/MoodTracker';
import ScheduleSharing from './src/components/ScheduleSharing';
import DailyAnalysis from './src/components/DailyAnalysis';

// App-wide color theme
export const COLORS = {
  primary: '#4A90E2',        // Light blue
  primaryDark: '#3B78C6',    // Darker blue for buttons/accents
  secondary: '#C0C0C0',      // Silver
  secondaryLight: '#E8E8E8', // Light silver (almost white)
  secondaryDark: '#9E9E9E',  // Dark silver (for text)
  white: '#FFFFFF',
  black: '#333333',
  success: '#4CAF50',        // Green
  warning: '#FFC107',        // Yellow
  danger: '#F44336',         // Red
  background: '#F5F7FA',     // Very light blue/silver background
  cardBackground: '#FFFFFF', // White for cards
};

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [category, setCategory] = useState('work'); // Default category
  const [dailyMood, setDailyMood] = useState({});
  const [analysis, setAnalysis] = useState(null);

  const addTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  const analyzeDailyPerformance = () => {
    // Generate insights based on completed tasks and mood data
    setAnalysis({
      productivityScore: 85,
      moodTrends: 'Positive after exercise tasks',
      suggestions: ['Schedule more morning exercise', 'Take breaks between long tasks']
    });
  };

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar barStyle="dark-content" />
      <ScrollView 
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
      >
        <Text style={styles.appTitle}>Schedule Bot</Text>

        <CategorySelector 
          selectedCategory={category} 
          onSelectCategory={setCategory} 
        />

        <Schedule 
          tasks={tasks} 
          category={category}
        />

        <TaskInput 
          onAddTask={addTask} 
          category={category}
        />

        <SuggestionPanel 
          tasks={tasks} 
          category={category}
        />

        <MoodTracker 
          onMoodUpdate={setDailyMood} 
        />

        <ScheduleSharing 
          schedule={tasks} 
        />

        <DailyAnalysis 
          analysis={analysis} 
          onAnalyze={analyzeDailyPerformance}
        />

        <View style={styles.footer}>
          <Text style={styles.footerText}>
            Schedule better, live smarter
          </Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: COLORS.background,
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    padding: 16,
    paddingTop: 40, // Add padding for status bar
    paddingBottom: 40,
  },
  appTitle: {
    fontSize: 28,
    fontWeight: 'bold',
    color: COLORS.primary,
    marginBottom: 20,
    textAlign: 'center',
  },
  footer: {
    marginTop: 30,
    alignItems: 'center',
  },
  footerText: {
    fontSize: 14,
    color: COLORS.secondaryDark,
    fontStyle: 'italic',
  }
});
