import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, StyleSheet, TouchableOpacity, ScrollView, Platform, Modal } from 'react-native';
import { Picker } from '@react-native-picker/picker';
import DateTimePicker from '@react-native-community/datetimepicker';
import COLORS from '../colors';

const TaskInput = ({ onAddTask }) => {
  const [title, setTitle] = useState('');
  const [duration, setDuration] = useState('');
  const [startTime, setStartTime] = useState('09:00');
  const [productivityLevel, setProductivityLevel] = useState('medium');
  const [selectedTags, setSelectedTags] = useState([]);
  const [showTimePicker, setShowTimePicker] = useState(false);

  const availableTags = ['work', 'leisure', 'grind', 'personal', 'meeting', 'health', 'learning'];

  // Format time for display (12-hour format with AM/PM)
  function getDisplayTime(timeString) {
    const [hours, minutes] = timeString.split(':').map(Number);
    const period = hours >= 12 ? 'PM' : 'AM';
    const displayHour = hours % 12 === 0 ? 12 : hours % 12;
    return `${displayHour}:${minutes.toString().padStart(2, '0')} ${period}`;
  }

  // Generate time options in 15-minute intervals
  const generateTimeOptions = () => {
    const options = [];
    for (let hour = 0; hour < 24; hour++) {
      for (let minute = 0; minute < 60; minute += 15) {
        const timeValue = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        const period = hour >= 12 ? 'PM' : 'AM';
        const displayHour = hour % 12 === 0 ? 12 : hour % 12;
        const displayTime = `${displayHour}:${minute.toString().padStart(2, '0')} ${period}`;

        options.push({
          value: timeValue,
          label: displayTime
        });
      }
    }
    return options;
  };

  const timeOptions = generateTimeOptions();

  const handleTagToggle = (tag) => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  // Convert time string to hours and minutes
  const timeToNumbers = (timeString) => {
    const [hours, minutes] = timeString.split(':').map(Number);
    return { hours, minutes };
  };

  // Convert hours and minutes to time string
  const numbersToTime = (hours, minutes) => {
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
  };

  const handleTimeChange = (event, selectedTime) => {
    setShowTimePicker(false);
    if (selectedTime) {
      const hours = selectedTime.getHours();
      const minutes = selectedTime.getMinutes();
      setStartTime(numbersToTime(hours, minutes));
    }
  };

  const showTimePickerModal = () => {
    setShowTimePicker(true);
  };

  const handleAddTask = () => {
    if (!title || !duration) {
      // Simple validation
      alert('Please fill out all required fields');
      return;
    }

    const newTask = {
      id: Date.now().toString(),
      title,
      duration: parseInt(duration, 10),
      startTime: new Date(`2023-01-01T${startTime}`).toISOString(),
      tags: selectedTags,
      productivityLevel,
      completed: false,
    };

    onAddTask(newTask);

    // Reset form
    setTitle('');
    setDuration('');
    setStartTime('09:00');
    setSelectedTags([]);
    setProductivityLevel('medium');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Add New Task</Text>

      <View style={styles.inputRow}>
        <Text style={styles.label}>Task Title:</Text>
        <TextInput
          style={styles.input}
          value={title}
          onChangeText={setTitle}
          placeholder="What are you planning to do?"
          placeholderTextColor={COLORS.secondaryDark}
        />
      </View>

      <View style={styles.inputRow}>
        <Text style={styles.label}>Duration (min):</Text>
        <TextInput
          style={styles.input}
          value={duration}
          onChangeText={setDuration}
          placeholder="How long will it take?"
          placeholderTextColor={COLORS.secondaryDark}
          keyboardType="numeric"
        />
      </View>

      <View style={styles.inputRow}>
        <Text style={styles.label}>Start Time:</Text>
        <View style={styles.timePickerContainer}>
          {Platform.OS === 'web' ? (
            <select
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              style={{
                width: '100%',
                padding: 8,
                borderRadius: 4,
                border: `1px solid ${COLORS.secondary}`,
                backgroundColor: COLORS.white,
                fontSize: 16,
              }}
            >
              {timeOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          ) : (
            <Picker
              selectedValue={startTime}
              style={styles.timePicker}
              onValueChange={(itemValue) => setStartTime(itemValue)}
            >
              {timeOptions.map(option => (
                <Picker.Item 
                  key={option.value} 
                  label={option.label} 
                  value={option.value} 
                />
              ))}
            </Picker>
          )}
        </View>
      </View>

      <View style={styles.tagsSection}>
        <Text style={styles.label}>Tags (optional):</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <View style={styles.tagsContainer}>
            {availableTags.map(tag => (
              <TouchableOpacity
                key={tag}
                style={[
                  styles.tagBadge,
                  selectedTags.includes(tag) ? { backgroundColor: getTagColor(tag) } : styles.unselectedTag
                ]}
                onPress={() => handleTagToggle(tag)}
              >
                <Text 
                  style={[
                    styles.tagText, 
                    selectedTags.includes(tag) ? styles.selectedTagText : styles.unselectedTagText
                  ]}
                >
                  {tag}
                </Text>
              </TouchableOpacity>
            ))}
          </View>
        </ScrollView>
      </View>


      <TouchableOpacity style={styles.addButton} onPress={handleAddTask}>
        <Text style={styles.addButtonText}>Add to Schedule</Text>
      </TouchableOpacity>
    </View>
  );
};

// Helper function to get color based on tag
const getTagColor = (tag) => {
  switch (tag.toLowerCase()) {
    case 'work': return COLORS.primary;
    case 'leisure': return COLORS.success;
    case 'grind': return COLORS.secondaryDark;
    case 'personal': return '#9C27B0'; // Purple
    case 'meeting': return '#FF9800'; // Orange
    case 'health': return '#E91E63'; // Pink
    case 'learning': return '#00BCD4'; // Cyan
    default: return COLORS.secondary;
  }
};

// Helper function to get color based on productivity level
const getProductivityColor = (level) => {
  switch (level) {
    case 'high': return COLORS.success;
    case 'medium': return COLORS.warning;
    case 'low': return COLORS.danger;
    default: return COLORS.secondaryDark;
  }
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: COLORS.cardBackground,
    borderRadius: 12,
    padding: 16,
    marginVertical: 10,
    shadowColor: COLORS.black,
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
    borderLeftWidth: 4,
    borderLeftColor: COLORS.primary,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 15,
    color: COLORS.black,
  },
  inputRow: {
    marginBottom: 12,
  },
  label: {
    fontSize: 14,
    fontWeight: '500',
    color: COLORS.secondaryDark,
    marginBottom: 5,
  },
  input: {
    borderWidth: 1,
    borderColor: COLORS.secondary,
    borderRadius: 6,
    padding: 10,
    fontSize: 16,
    backgroundColor: COLORS.white,
    color: COLORS.black,
  },
  timePickerContainer: {
    flex: 1,
    borderWidth: Platform.OS !== 'web' ? 1 : 0,
    borderColor: COLORS.secondary,
    borderRadius: 6,
    backgroundColor: COLORS.white,
    overflow: 'hidden',
  },
  timePicker: {
    width: '100%',
    height: 50,
  },
  tagsSection: {
    marginVertical: 12,
  },
  tagsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginTop: 5,
  },
  tagBadge: {
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 20,
    marginRight: 8,
    marginBottom: 8,
  },
  unselectedTag: {
    backgroundColor: COLORS.secondaryLight,
    borderWidth: 1,
    borderColor: COLORS.secondary,
  },
  tagText: {
    fontSize: 14,
    fontWeight: '500',
  },
  selectedTagText: {
    color: COLORS.white,
  },
  unselectedTagText: {
    color: COLORS.secondaryDark,
  },
  productivityContainer: {
    marginVertical: 12,
  },
  levelButtons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  levelButton: {
    flex: 1,
    paddingVertical: 8,
    borderRadius: 6,
    marginHorizontal: 4,
    alignItems: 'center',
  },
  selectedLevel: {
    borderWidth: 2,
    borderColor: COLORS.secondaryDark,
  },
  levelText: {
    color: COLORS.white,
    fontWeight: '600',
  },
  addButton: {
    backgroundColor: COLORS.primary,
    paddingVertical: 12,
    borderRadius: 6,
    alignItems: 'center',
    marginTop: 10,
  },
  addButtonText: {
    color: COLORS.white,
    fontWeight: 'bold',
    fontSize: 16,
  },
  modalOverlay: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    width: '80%',
    maxHeight: '80%',
    backgroundColor: COLORS.white,
    borderRadius: 10,
    padding: 20,
    alignItems: 'center',
  },
  modalTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 15,
    color: COLORS.primary,
  },
  timeOptionsContainer: {
    width: '100%',
    maxHeight: 300,
  },
  timeOption: {
    padding: 12,
    borderBottomWidth: 1,
    borderBottomColor: COLORS.lightGray,
  },
  selectedTimeOption: {
    backgroundColor: COLORS.lightPrimary,
  },
  timeOptionText: {
    fontSize: 16,
  },
  selectedTimeOptionText: {
    fontWeight: 'bold',
    color: COLORS.primary,
  },
  closeButton: {
    marginTop: 15,
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: COLORS.primary,
    borderRadius: 5,
  },
  closeButtonText: {
    color: COLORS.white,
    fontWeight: 'bold',
  },
});

export default TaskInput;
